import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

/**
 * ResultsSection component
 * Displays the price comparison results
 * @param {Object} props - Component props
 * @param {Object} props.results - The price comparison results
 */
function ResultsSection({ results }) {
  const formatPrice = (price) => {
    return price ? `₹${price.toFixed(2)}` : 'N/A';
  };

  const chartData = {
    labels: results.priceHistory.map(p => new Date(p.date).toLocaleDateString()),
    datasets: [
      {
        label: 'Price History',
        data: results.priceHistory.map(p => p.price),
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Price History'
      }
    }
  };

  return (
    <div className="results">
      <h2>{results.title}</h2>
      <img src={results.image} alt={results.title} />
      <ul className="price-list">
        <li className="price-item">
          <span className="price-source">{results.source}</span>
          <span className="price-value">{formatPrice(results.sourcePrice)}</span>
        </li>
        <li className="price-item">
          <span className="price-source">Amazon</span>
          <span className="price-value">{formatPrice(results.amazonPrice)}</span>
        </li>
        <li className="price-item">
          <span className="price-source">Flipkart</span>
          <span className="price-value">{formatPrice(results.flipkartPrice)}</span>
        </li>
        <li className="price-item">
          <span className="price-source">eBay</span>
          <span className="price-value">{formatPrice(results.ebayPrice)}</span>
        </li>
        <li className="price-item">
          <span className="price-source">Myntra</span>
          <span className="price-value">{formatPrice(results.myntraPrice)}</span>
        </li>
      </ul>
      <div className="price-history">
        <h3>Price History</h3>
        <div className="price-extremes">
          <div className="price-extreme">
            <div className="price-extreme-label">Highest price this year</div>
            <div className="price-extreme-value">{formatPrice(results.maxPrice)}</div>
          </div>
          <div className="price-extreme">
            <div className="price-extreme-label">Lowest price this year</div>
            <div className="price-extreme-value">{formatPrice(results.minPrice)}</div>
          </div>
        </div>
        <div style={{ width: '100%', height: '300px' }}>
          <Line data={chartData} options={chartOptions} />
        </div>
      </div>
    </div>
  );
}

export default ResultsSection;
