import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

/**
 * ResultsSection component
 * Displays the price comparison results
 * @param {Object} props - Component props
 * @param {Object} props.results - The price comparison results
 */
function ResultsSection({ results }) {
  const formatPrice = (price) => {
    return price ? `₹${price.toFixed(2)}` : 'N/A';
  };

  const chartData = {
    labels: results.priceHistory.map(p => new Date(p.date).toLocaleDateString()),
    datasets: [
      {
        label: 'Price History',
        data: results.priceHistory.map(p => p.price),
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Price History'
      }
    }
  };

  return (
    <div className="results">
      <h2>{results.title}</h2>
      <img src={results.image} alt={results.title} />
      <ul>
        <li>{results.source}: {formatPrice(results.sourcePrice)}</li>
        <li>Amazon: {formatPrice(results.amazonPrice)}</li>
        <li>Flipkart: {formatPrice(results.flipkartPrice)}</li>
        <li>eBay: {formatPrice(results.ebayPrice)}</li>
        <li>Myntra: {formatPrice(results.myntraPrice)}</li>
      </ul>
      <div>
        <p>Highest price this year: {formatPrice(results.maxPrice)}</p>
        <p>Lowest price this year: {formatPrice(results.minPrice)}</p>
      </div>
      <div style={{ width: '100%', height: '300px' }}>
        <Line data={chartData} options={chartOptions} />
      </div>
    </div>
  );
}

export default ResultsSection;
